<template>    <div id="teamPage">        <van-search v-model="searchText" @search="onSearch" placeholder="搜索队伍" />        <van-button type="primary" @click="doJoinTeam">加入队伍</van-button>        <team-card-list :teamList="teamList" />        <van-empty v-if="teamList?.length <1" description="数据为空"/>    </div></template><script setup lang="ts">    import {useRouter} from "vue-router";    import {onMounted, ref} from 'vue';    import myAxios from "../plugins/myAxios";    import {Toast} from "vant";    import TeamCardList from "../components/TeamCardList.vue"    const router = useRouter();    //跳转到加入队伍列    const doJoinTeam = () => {        router.push({            path: "/team/add"        })    }    const searchText = ref('')    const onSearch = (val) => {        listTeam(val)    }    const myJoinTeamList = ref([]);    /**     * 搜索我加入的队伍     * @param val     */    const listMyJoinTeam = async (val = '') =>{        const res = await myAxios.get('/team/list/my/join',{            params:{                searchText:val            }        });        if(res?.code === 0){            myJoinTeamList.value = res.data;        }        else{            Toast.fail('加载队伍失败，请刷新重试')        }    }    const listTeam = async (val = '') =>{        const res = await myAxios.get('/team/list',{            params:{                searchText:val            }        });        if(res?.code === 0){            teamList.value = res.data;        }        else{            Toast.fail('加载队伍失败，请刷新重试')        }    }    const teamList = ref([]);    //只会在页面加载时，触发一次    onMounted(async () => {        listTeam('');    })</script><style scoped>    #teamPage {    }</style>